import React, { useState } from "react";
import Axios from "axios";
import './App.css';

function App() {
  const [data, setData] = useState([]);
  const [search, setSearch] = useState("");

  async function submit() {
    try {
      const response = await Axios.get(`http://localhost:5000/?search=${search}`);
      setData(response.data);
    } catch (error) {
      console.log(error.message);
    }
  }

  function handler(e) {
    e.preventDefault();
    submit(search);
  }

  return (
    <div className="App">
      <form onSubmit={handler}>
        <input onChange={e => { setSearch(e.target.value) }} value={search} placeholder="Search Here" type="text"></input>
        <button type="submit">Submit</button>
      </form>

      {data.map((item, index) => (
        <div className="within" key={index}>
          <p>Title: {item.title}</p>
          <p>Title (English): {item.title_english}</p>
          <p>Year: {item.year}</p>
          <p>Rating: {item.rating}</p>
          <img src={item.large_cover_image} alt={`Cover for ${item.title}`} />
          <img src={item.background_image_original} alt={`Background for ${item.title}`} />
          {/* Add alt text and unique keys for other images if needed */}
        </div>
      ))}
    </div>
  );
}

export default App;
