import React, { useState, useEffect, useCallback } from "react";
import Axios from "axios";
import "./App.css";

export default function App() {
  const [data, setData] = useState([]);
  const [search, setSearch] = useState("");
  const [quality, setQuality] = useState("all");
  const [limit, setLimit] = useState(20);

  useEffect(() => {

    submitx(search, quality, limit);
  }, [search, quality, limit]);

  async function submitx(query_term, qual, lim) {
    try {
      const response = await Axios.get(
        `http://localhost:5000/?query_term=${query_term}&quality=${qual}&limit=${lim}`
      );
      setData(response.data);
    } catch (error) {
      console.log(error.message);
    }
  }



  function handler(e) {
    e.preventDefault();
    submitx(search, quality, limit);
  }

  return (
    <div className="App">
      <form onSubmit={handler}>
        <h1>Hit Enter Again, if the collected outcome is not being displayed!</h1>
        <label htmlFor="membership">Select Quality:</label>
        <select
          name="membership"
          id="membership"
          onChange={(e) => {
            setQuality(e.target.value);
          }}
          value={quality}
        >
          <option value="all">All</option> 
          <option value="3D">3D</option>
          <option value="1080p">1080p</option>
          <option value="720p">720p</option>
        </select>
        <input
          onChange={(e) => {
            setSearch(e.target.value);
          }}
          value={search}
          placeholder="Search Here"
          type="text"
          className="searchbar"
        ></input>
        <select
          onChange={(e) => {
            setLimit(e.target.value);
          }}
          value={limit}
        >
          <option value="20">20</option>
        </select>
        <button type="submit">Submit</button>
        {data.length === 0 ? (
          <p>No results found</p>
        ) : (
          data.map((item) => (
            <div className="within" key={item.id}>
              <h1>Title: {item.title || "Unavailable"}</h1>
              {/* ... (other content) ... */}
            </div>
          ))
        )}
      </form>
    </div>
  );
}
