import React,{useState} from "react";
import Axios from "axios";
import './App.css';

function App() {
  const [data,setData] = useState([]);
  const [search,setSearch] = useState("");



  async function submit(){
      try{
        const r = await Axios.get("http://localhost:5000/");
        setData(r.data);
      }catch(error){
        console.log(error.message);
      }
  };

  function handler(e){
      e.preventDefault();
      submit(search);
  };

  return (
    <div className="App">
       <form onSubmit={handler}>
        <input onChange={e=>{setSearch(e.target.value)}} value={search} placeholder="Search Here" type="text"></input>
        {data.map((key,index)=>{
          <div className="within" key={key}>
            <p> Title : {index.title}</p>
            <p>{index.title_english}</p>
            <p>{index.year}</p>
            <p>{index.year}</p>
            <img src={index.large_cover_image}></img>
            <img src={index.background_image_original}></img>
            <img ></img>
          </div>
        })}
      <button type="submit">Submit</button>
       </form>
    </div>
  );
}

export default App;
