import React, { useState } from "react";
import Axios from "axios";
import "./App.css";

function App() {
  const [data, setData] = useState([]);
  const [search, setSearch] = useState("");
  const [quality, setQuality] = useState("720p"); // Set a default quality

  async function submitx(query_term) {
    try {
      const response = await Axios.get(
        `http://localhost:5000/?query_term=${query_term}&quality=${quality}` // Use "&" to separate query parameters
      );

      setData(response.data);
    } catch (error) {
      console.log(error.message);
    }
  }

  function handleQualityChange(e) {
    setQuality(e.target.value); // Update the quality state when the dropdown selection changes
  }

  function handleSubmit(e) {
    e.preventDefault();
    submitx(search);
  }

  return (
    <div className="App">
      <form onSubmit={handleSubmit}>
        <label htmlFor="membership">Choose a membership plan:</label>
        <select
          name="membership"
          id="membership"
          value={quality} // Set the selected value based on the "quality" state
          onChange={handleQualityChange} // Handle quality selection changes
        >
          <option value="3d">3D</option>
          <option value="1080p">1080p</option>
          <option value="720p">720p</option>
        </select>
        <input
          onChange={(e) => {
            setSearch(e.target.value);
          }}
          value={search}
          placeholder="Search Here"
          type="text"
          className="searchbar"
        ></input>

        {data.length === 0 ? (
          <p>No results found</p>
        ) : (
          data.map((item) => (
            <div className="within" key={item.id}>
              {/* ... Rest of your code ... */}
            </div>
          ))
        )}
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default App;
