import React, { useState, useEffect } from "react";
import Axios from "axios";
import "./App.css";

function App() {
  const [data, setData] = useState([]);
  const [search, setSearch] = useState("");



  async function submitx(searchTerm) {
    try {
      const response = await Axios.get(`http://localhost:5000/`, {
        searchTerm,
      });
      setData(response.data);
    } catch (error) {
      console.log(error.message);
    }
  }

  function handler(e) {
    e.preventDefault();
    submitx(search);
  }

  return (
    <div className="App">
      <form onSubmit={handler}>
        <input
          onChange={(e) => {
            setSearch(e.target.value);
          }}
          value={search}
          placeholder="Search Here"
          type="text"
        ></input>

        {data.length === 0 ? (
        <p>No results found</p>
      ) : (
        data.map((item) => (
          <div className="within" key={item.id}>
            <p>Title: {item.title}</p>
            <p> {item.summary}</p>
            <p>Year: {item.year}</p>
            <p>Rating: {item.rating}</p>
            <img src={item.large_cover_image} alt={`Cover for ${item.title}`} />
            <img
              src={item.background_image_original}
              alt={`Background for ${item.title}`}
            />
            <p> Runtime : {item.runtime} mins</p>
          </div>
        ))
      )}
        <button type="submit">Submit</button>
      </form>


    </div>
  );
}

export default App;
