import React, { useState, useEffect } from "react";
import Axios from "axios";
import "./App.css";

function App() {
  const [data, setData] = useState([]);
  const [search, setSearch] = useState("");

  async function submitx(searchTerm) {
    try {
      const response = await Axios.get(`http://localhost:5000/`, {
        searchTerm,
      });
      setData(response.data);
    } catch (error) {
      console.log(error.message);
    }
  }

  function handler(e) {
    e.preventDefault();
    submitx(search);
  }

  return (
    <div className="App">
      <form onSubmit={handler}>
        <input
          onChange={(e) => {
            setSearch(e.target.value);
          }}
          value={search}
          placeholder="Search Here"
          type="text"
        ></input>

        {data.length === 0 ? (
          <p>No results found</p>
        ) : (
          data.map((item) => (
            <div className="within" key={item.id}>
              <p>Title: {item.title}</p>
              <img
                src={item.large_cover_image}
                alt={`Cover for ${item.title}`}
              />
              <br></br>
              <p> {item.summary}</p>
              <p>Year: {item.year}</p>
              <p>Rating: {item.rating}</p>

              <img
                src={item.background_image_original || "Unavailable"}
                alt={`Background for ${item.title}`}
              />
              <p> Genre:{item.genres || "Unavailable"}</p>
              <p> Torrent Download : {item.torrents.url || "Unavailable"}</p>
              <p> Quality : {item.torrents.quality || "Unavailable"}</p>
              <p>Size : {item.torrents.size || "Unavailable"}</p>
              <p> Uploaded On : {item.torrents.date_uploaded || "Unavailable"}</p>
              <p> Runtime : {item.runtime || "Unavailable"} mins</p>
            </div>
          ))
        )}
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default App;
